package com.janbask.arithmeticOperators;

public class Decrement_Operator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int l=200;
		
		l--;// 199
		l--;//198
	//	l=l-1; // 197
		
		l--;//197
		l=l-3;//194
		l--;//193
		l=l-2;//191
		l--;//190
		l=l-200;//-10
		
		System.out.println(l);//-10

	}

}
